import React from "react";
import bannerImage from "../images/banner.jpg"; // Import the image

const Banner = () => {
  return (
    <div style={styles.banner}>
      <h1 style={styles.title}>The Easiest Way To Get Your Dream Job</h1>
      <p style={styles.subtitle}>
        Lorem ipsum dolor sit amet, consectetur adipisicing elit. Cupiditate
        est, consequuntur perferendis.
      </p>
      <div style={styles.searchContainer}>
        <input
          type="text"
          placeholder="Job title, Company..."
          style={styles.input}
        />
        <select style={styles.select}>
          <option value="">Select Region</option>
          <option value="region1">Harare</option>
          <option value="region2">Bulawayo</option>
          <option value="region3">Gweru</option>
          <option value="region4">Mutare</option>
          <option value="region5">Masvingo</option>
        </select>
        <select style={styles.select}>
          <option value="">Select Job Type</option>
          <option value="full-time">Full Time</option>
          <option value="part-time">Part Time</option>
        </select>
        <button style={styles.button}>Search Job</button>
      </div>
      <button style={styles.getStartedButton}>Get Started</button>
    </div>
  );
};

const styles = {
  banner: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    textAlign: "center",
    padding: "100px 20px",
    color: "white",
    backgroundImage: `url(${bannerImage})`,
    height: "80vh",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundColor: "rgba(0, 0, 0, 0.5)", // Dark overlay
    backgroundBlendMode: "multiply", // Blend the background color with the image
  },
  title: {
    fontSize: "2.5rem",
    marginBottom: "20px",
  },
  subtitle: {
    fontSize: "1.2rem",
    marginBottom: "30px",
  },
  searchContainer: {
    display: "flex",
    flexDirection: "column", // Stack elements on smaller screens
    alignItems: "center",
    gap: "10px",
    marginBottom: "20px",
    width: "100%", // Full width for responsiveness
  },
  input: {
    padding: "10px",
    fontSize: "1rem",
    width: "80%", // Responsive width
    maxWidth: "300px", // Max width for larger screens
  },
  select: {
    padding: "10px",
    fontSize: "1rem",
    width: "80%", // Responsive width
    maxWidth: "150px", // Max width for larger screens
  },
  button: {
    padding: "10px 20px",
    fontSize: "1rem",
    backgroundColor: "#007BFF",
    color: "white",
    border: "none",
    cursor: "pointer",
  },
  getStartedButton: {
    padding: "10px 20px",
    fontSize: "1rem",
    backgroundColor: "#007BFF",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

// Media queries for responsiveness
const mediaQueries = {
  "@media (min-width: 768px)": {
    searchContainer: {
      flexDirection: "row", // Row layout on larger screens
    },
  },
};

export default Banner;
