import React from "react";
import { Card, Col, Row } from "antd";

const Category = () => {
  const jobs = [
    { title: "Marketing", vacancies: "123 Vacancy" },
    { title: "Customer Service", vacancies: "123 Vacancy" },
    { title: "Human Resource", vacancies: "123 Vacancy" },
    { title: "Project Management", vacancies: "123 Vacancy" },
    { title: "Business Development", vacancies: "123 Vacancy" },
    { title: "Sales & Communication", vacancies: "123 Vacancy" },
    { title: "Teaching & Education", vacancies: "123 Vacancy" },
    { title: "Design & Creative", vacancies: "123 Vacancy" },
  ];

  const boxStyle = {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "space-around",
    padding: "20px",
  };

  const cardStyle = {
    backgroundColor: "#f9f9f9",
    border: "1px solid #e0e0e0",
    borderRadius: "8px",
    width: "200px",
    margin: "10px",
    textAlign: "center",
    padding: "20px",
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
    transition: "transform 0.2s",
  };

  const cardHoverStyle = {
    transform: "scale(1.05)",
  };

  return (
    <div style={boxStyle}>
      {jobs.map((job, index) => (
        <div
          key={index}
          style={cardStyle}
          onMouseEnter={(e) =>
            (e.currentTarget.style.transform = "scale(1.05)")
          }
          onMouseLeave={(e) => (e.currentTarget.style.transform = "scale(1)")}
        >
          <h3>{job.title}</h3>
          <p>{job.vacancies}</p>
        </div>
      ))}
    </div>
  );
};

export default Category;
