import React, { useState } from "react";

const Navbar = () => {
  const [isMobileMenuOpen, setMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <nav style={styles.navbar}>
      <div style={styles.logo}>StarJobs Zim</div>
      <div style={styles.mobileIcon} onClick={toggleMobileMenu}>
        &#9776; {/* Hamburger icon */}
      </div>
      <ul
        style={{
          ...styles.navLinks,
          ...(isMobileMenuOpen ? styles.mobileMenu : {}),
        }}
      >
        <li>
          <a href="/" style={styles.link}>
            Home
          </a>
        </li>
        <li>
          <a href="/jobs" style={styles.link}>
            Jobs
          </a>
        </li>
        <li>
          <a href="/about" style={styles.link}>
            About
          </a>
        </li>
        <li>
          <a href="/contact" style={styles.link}>
            Contact
          </a>
        </li>
      </ul>
    </nav>
  );
};

const styles = {
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "10px 20px",
    backgroundColor: "#007BFF",
    color: "white",
    flexWrap: "wrap",
  },
  logo: {
    fontSize: "1.5rem",
    fontWeight: "bold",
  },
  navLinks: {
    listStyle: "none",
    display: "flex",
    gap: "20px",
    margin: 0,
    padding: 0,
  },
  link: {
    color: "white",
    textDecoration: "none",
    fontSize: "1rem",
  },
  mobileMenu: {
    display: "flex",
    flexDirection: "column",
    width: "100%",
    position: "absolute",
    top: "60px", // Adjust based on navbar height
    left: 0,
    backgroundColor: "#007BFF",
  },
  mobileIcon: {
    display: "none",
    cursor: "pointer",
    fontSize: "1.5rem",
  },
};

// Add media queries for responsiveness
const mediaQueries = {
  "@media (max-width: 768px)": {
    navLinks: {
      display: "none",
    },
    mobileIcon: {
      display: "block",
    },
  },
};

export default Navbar;
