import React, { useState } from "react";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav style={styles.navbar}>
      <div style={styles.logo}>StarJobs Zim</div>
      <div style={styles.hamburger} onClick={toggleMenu}>
        &#9776; {/* Hamburger icon */}
      </div>
      <ul style={{ ...styles.navLinks, display: isOpen ? "flex" : "none" }}>
        <li>
          <a href="/" style={styles.link}>
            Home
          </a>
        </li>
        <li>
          <a href="/jobs" style={styles.link}>
            Jobs
          </a>
        </li>
        <li>
          <a href="/about" style={styles.link}>
            About
          </a>
        </li>
        <li>
          <a href="/contact" style={styles.link}>
            Contact
          </a>
        </li>
      </ul>
    </nav>
  );
};

const styles = {
  navbar: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "10px 20px",
    backgroundColor: "#007BFF",
    color: "white",
    position: "relative",
  },
  logo: {
    fontSize: "1.5rem",
    fontWeight: "bold",
  },
  hamburger: {
    display: "none",
    fontSize: "1.5rem",
    cursor: "pointer",
  },
  navLinks: {
    listStyle: "none",
    display: "flex",
    gap: "20px",
  },
  link: {
    color: "white",
    textDecoration: "none",
    fontSize: "1rem",
  },
  "@media (max-width: 768px)": {
    navLinks: {
      flexDirection: "column",
      position: "absolute",
      top: "60px",
      left: "0",
      right: "0",
      backgroundColor: "#007BFF",
      padding: "10px 0",
    },
    hamburger: {
      display: "block",
    },
  },
};

export default Navbar;
