import React from "react";
import bannerImage from "../images/banner.jpg"; // Import the image

const Banner = () => {
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        padding: "100px 20px",
        color: "white",
        backgroundImage: `url(${bannerImage})`,
        height: "80vh",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundColor: "rgba(0, 0, 0, 0.5)", // Dark overlay
        backgroundBlendMode: "multiply", // Blend the background color with the image
      }}
    >
      <h1 style={styles.title}>The Easiest Way To Get Your Dream Job</h1>
      <p style={styles.paragraph}>
        Lorem ipsum dolor sit amet, consectetur adipisicing elit. Cupiditate
        est, consequuntur perferendis.
      </p>
      <div style={styles.searchContainer}>
        <input
          type="text"
          placeholder="Job title, Company..."
          style={styles.input}
        />
        <select style={styles.select}>
          <option value="">Select Region</option>
          <option value="region1">Harare</option>
          <option value="region2">Bulawayo</option>
          <option value="region3">Gweru</option>
          <option value="region4">Mutare</option>
          <option value="region5">Masvingo</option>
        </select>
        <select style={styles.select}>
          <option value="">Select Job Type</option>
          <option value="full-time">Full Time</option>
          <option value="part-time">Part Time</option>
        </select>
        <button style={styles.button}>Search Job</button>
      </div>
      <button style={styles.getStartedButton}>Get Started</button>
    </div>
  );
};

const styles = {
  title: {
    fontSize: "2.5rem",
    marginBottom: "20px",
  },
  paragraph: {
    fontSize: "1.2rem",
    marginBottom: "30px",
  },
  searchContainer: {
    display: "flex",
    justifyContent: "center",
    gap: "10px",
    marginBottom: "20px",
  },
  input: {
    padding: "10px",
    fontSize: "1rem",
    width: "300px",
  },
  select: {
    padding: "10px",
    fontSize: "1rem",
  },
  button: {
    padding: "10px 20px",
    fontSize: "1rem",
    backgroundColor: "#007BFF",
    color: "white",
    border: "none",
    cursor: "pointer",
  },
  getStartedButton: {
    padding: "10px 20px",
    fontSize: "1rem",
    backgroundColor: "#007BFF",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

// Media queries for responsiveness
const mediaQueries = {
  "@media (max-width: 600px)": {
    title: {
      fontSize: "1.8rem",
    },
    paragraph: {
      fontSize: "1rem",
    },
    input: {
      width: "90%",
    },
    select: {
      width: "90%",
    },
    searchContainer: {
      flexDirection: "column",
      alignItems: "stretch",
    },
  },
};

// Apply media queries to styles
const applyMediaQueries = (baseStyles) => {
  const newStyles = { ...baseStyles };
  Object.keys(mediaQueries).forEach((key) => {
    const stylesKey = mediaQueries[key];
    Object.keys(stylesKey).forEach((styleKey) => {
      newStyles[styleKey] = { ...newStyles[styleKey], ...stylesKey[styleKey] };
    });
  });
  return newStyles;
};

const responsiveStyles = applyMediaQueries(styles);

export default () => <Banner styles={responsiveStyles} />;
