import React from "react";
import Button from "@mui/material/Button";
import jobImage from "../images/we-are-hiring-concept-collage.jpg";

const JobList = () => {
  // Example job data
  const jobs = [
    {
      id: 1,
      title: "Software Engineer",
      company: "Tech Corp",
      region: "Remote",
      type: "Full Time",
    },
    {
      id: 2,
      title: "Product Manager",
      company: "Innovate Inc",
      region: "Bulawayo",
      type: "Full Time",
    },
    {
      id: 3,
      title: "Data Analyst",
      company: "Data Works",
      region: "Harare",
      type: "Part Time",
    },
  ];

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "row",
        alignItems: "flex-start",
        justifyContent: "space-between",
        // Responsive layout for smaller screens
        "@media (max-width: 768px)": {
          flexDirection: "column",
        },
      }}
    >
      <div style={{ flex: 1 }}>
        <h2 style={{ textAlign: "left", marginLeft: "30px" }}>RECENT JOBS</h2>
        <div style={styles.jobList}>
          {jobs.map((job) => (
            <div key={job.id} style={styles.jobItem}>
              <h2>{job.title}</h2>
              <p>{job.company}</p>
              <p>
                {job.region} - {job.type}
              </p>
            </div>
          ))}
          <div>
            <Button variant="contained" style={{ padding: "10px" }}>
              Get More Jobs
            </Button>
          </div>
        </div>
      </div>
      <div
        style={{
          flex: 1,
          textAlign: "center",
          // Responsive layout for smaller screens
          "@media (max-width: 768px)": {
            order: 1, // Move the image to the bottom
          },
        }}
      >
        <img
          src={jobImage}
          alt="Job Illustration"
          style={{
            maxWidth: "70%",
            height: "600px",
            borderRadius: "5px",
            marginTop: "85px",
            // Responsive styling for smaller screens
            "@media (max-width: 768px)": {
              maxWidth: "100%",
              height: "auto",
              marginTop: "20px",
            },
          }}
        />
      </div>
    </div>
  );
};

const styles = {
  jobList: {
    display: "flex",
    flexDirection: "column",
    marginLeft: "20px",
  },
  jobItem: {
    border: "1px solid #ddd",
    borderRadius: "5px",
    padding: "20px",
    margin: "10px",
    width: "100%", // Adjusted to take full width of the container
    textAlign: "left",
  },
};

export default JobList;
