import React from "react";

const Banner = () => {
  return (
    <div style={styles.banner}>
      <h1 style={styles.title}>The Easiest Way To Get Your Dream Job</h1>
      <p style={styles.description}>
        Lorem ipsum dolor sit amet, consectetur adipisicing elit. Cupiditate
        est, consequuntur perferendis.
      </p>
      <button style={styles.button}>Get Started</button>
    </div>
  );
};

const styles = {
  banner: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    textAlign: "center",
    padding: "100px 20px",
    backgroundColor: "#f8f9fa",
    color: "#333",
  },
  title: {
    fontSize: "2.5rem",
    marginBottom: "20px",
  },
  description: {
    fontSize: "1.2rem",
    marginBottom: "30px",
  },
  button: {
    padding: "10px 20px",
    fontSize: "1rem",
    backgroundColor: "#007BFF",
    color: "white",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default Banner;
